    46} from "@/api/settings/UserSettingsTopic";
    47
    48export type TopicInstance =
    49  | WebsocketTopic<MarketDataMessage>
    50  | WebsocketTopic<SolanaPriceMessage>
    51  | WebsocketTopic<SolBalanceMessage>
    52  | WebsocketTopic<SwapTopicMessage>
    53  | WebsocketTopic<TokenPnlMessage>
    54  | WebsocketTopic<LimitOrderMessage>
    55  | WebsocketTopic<NotificationMessage>
    56  | WebsocketTopic<TradeMessage>
    57  | WebsocketTopic<OHLCVMessage>
    58  | WebsocketTopic<TokenEventMessage>
    59  | WebsocketTopic<HoldersMessage>
    60  | WebsocketTopic<TransferTopicMessage>
    61  | WebsocketTopic<WithdrawalMessage>
    62  | WebsocketTopic<LimitOrderCompleteMessage>
    63  | WebsocketTopic<UserSettingsMessage>;
    64
    65type TopicConfig = {
    66  isTopicType: (topic: TopicName) => boolean;
    67  TopicClass: new (args: {
    68    ws: WebSocket;
    69    topic: TopicName;
    70    store: StoreApi<GlobalStore>;
    71    persistentStore: StoreApi<PersistentGlobalStore>;
    72    queryClient: QueryClient;
    73    args?: Record<string, unknown>;
    74    keepAliveInterval?: number;
    75  }) => TopicInstance;
    76};
    77
    78const TOPIC_CONFIGS: TopicConfig[] = [
    79  {
    80    isTopicType: MarketDataTopic.isMarketDataTopic,
    81    TopicClass: MarketDataTopic,
    82  },
    83  {
    84    isTopicType: TradesTopic.isTradesTopic,
    85    TopicClass: TradesTopic,
    86  },
    87  {
    88    isTopicType: OhlcvTopic.isOhlcvTopic,
    89    TopicClass: OhlcvTopic,
    90  },
    91  {
    92    isTopicType: TokenEventsTopic.isTokenEventsTopic,
    93    TopicClass: TokenEventsTopic,
    94  },
    95  {
    96    isTopicType: SolPriceTopic.isSolPriceTopic,
    97    TopicClass: SolPriceTopic,
    98  },
    99  {
   100    isTopicType: SolBalanceTopic.isSolBalanceTopic,
   101    TopicClass: SolBalanceTopic,
   102  },
   103  {
   104    isTopicType: SwapTopic.isSwapTopic,
   105    TopicClass: SwapTopic,
   106  },
   107  {
   108    isTopicType: PnlTopic.isPnlTopic,
   109    TopicClass: PnlTopic,
   110  },
   111  {
   112    isTopicType: LimitOrderTopic.isLimitOrderTopic,
   113    TopicClass: LimitOrderTopic,
   114  },
   115  {
   116    isTopicType: LimitOrderCompleteTopic.isLimitOrderCompleteTopic,
   117    TopicClass: LimitOrderCompleteTopic,
   118  },
   119  {
   120    isTopicType: WebNotificationsTopic.isWebNotificationsTopic,
   121    TopicClass: WebNotificationsTopic,
   122  },
   123  {
   124    isTopicType: HoldersTopic.isHoldersTopic,
   125    TopicClass: HoldersTopic,
   126  },
   127  {
   128    isTopicType: TransferTopic.isTransferTopic,
   129    TopicClass: TransferTopic,
   130  },
   131  {
   132    isTopicType: WithdrawalTopic.isWithdrawalTopic,
   133    TopicClass: WithdrawalTopic,
   134  },
   135  {
   136    isTopicType: UserSettingsTopic.isUserSettingsTopic,
   137    TopicClass: UserSettingsTopic,
   138  },
   139];
   140
   141/**
   142 * A factory function to create the correct topic instance based on the topic
   143 * name.
   144 */
   145export const createTopic = (args: {
   146  ws: WebSocket;
   147  topic: TopicName;
   148  store: StoreApi<GlobalStore>;
   149  persistentStore: StoreApi<PersistentGlobalStore>;
   150  queryClient: QueryClient;
   151  args?: Record<string, unknown>;
   152  keepAliveInterval?: number;
   153}): TopicInstance => {
   154  for (const config of TOPIC_CONFIGS) {
   155    if (config.isTopicType(args.topic)) {
   156      return new config.TopicClass(args);
   157    }
   158  }
   159
   160  // This should only happen if you forgot to add a new topic to the config
   161  throw new Error(`Unknown topic: ${args.topic}`);
   162};
