import { QueryClient } from "@tanstack/query-core";
import { StoreApi } from "zustand";
import { PersistentGlobalStore } from "@/store/persistentStore/types";
import { HoldersTopic } from "@/api/holders/HoldersTopic";
import { TransferTopic } from "@/api/transfer/TransferTopic.ts";
import { TransferTopicMessage } from "@/api/transfer/types.ts";
import {
  WithdrawalTopic,
  WithdrawalMessage,
} from "@/api/withdrawal/WithdrawalTopic";
import {
  UserSettingsTopic,
  UserSettingsMessage,
} from "@/api/settings/UserSettingsTopic";

export type TopicInstance =
  | WebsocketTopic<MarketDataMessage>
  | WebsocketTopic<SolanaPriceMessage>
  | WebsocketTopic<SolBalanceMessage>
  | WebsocketTopic<SwapTopicMessage>
  | WebsocketTopic<TokenPnlMessage>
  | WebsocketTopic<LimitOrderMessage>
  | WebsocketTopic<NotificationMessage>
  | WebsocketTopic<TradeMessage>
  | WebsocketTopic<OHLCVMessage>
  | WebsocketTopic<TokenEventMessage>
  | WebsocketTopic<HoldersMessage>
  | WebsocketTopic<TransferTopicMessage>
  | WebsocketTopic<WithdrawalMessage>
  | WebsocketTopic<LimitOrderCompleteMessage>
  | WebsocketTopic<UserSettingsMessage>;

type TopicConfig = {
  isTopicType: (topic: TopicName) => boolean;
  TopicClass: new (args: {
    ws: WebSocket;
    topic: TopicName;
    store: StoreApi<GlobalStore>;
    persistentStore: StoreApi<PersistentGlobalStore>;
    queryClient: QueryClient;
    args?: Record<string, unknown>;
    keepAliveInterval?: number;
  }) => TopicInstance;
};

const TOPIC_CONFIGS: TopicConfig[] = [
  {
    isTopicType: MarketDataTopic.isMarketDataTopic,
    TopicClass: MarketDataTopic,
  },
  {
    isTopicType: TradesTopic.isTradesTopic,
    TopicClass: TradesTopic,
  },
  {
    isTopicType: OhlcvTopic.isOhlcvTopic,
    TopicClass: OhlcvTopic,
  },
  {
    isTopicType: TokenEventsTopic.isTokenEventsTopic,
    TopicClass: TokenEventsTopic,
  },
  {
    isTopicType: SolPriceTopic.isSolPriceTopic,
    TopicClass: SolPriceTopic,
  },
  {
    isTopicType: SolBalanceTopic.isSolBalanceTopic,
    TopicClass: SolBalanceTopic,
  },
  {
    isTopicType: SwapTopic.isSwapTopic,
    TopicClass: SwapTopic,
  },
  {
    isTopicType: PnlTopic.isPnlTopic,
    TopicClass: PnlTopic,
  },
  {
    isTopicType: LimitOrderTopic.isLimitOrderTopic,
    TopicClass: LimitOrderTopic,
  },
  {
    isTopicType: LimitOrderCompleteTopic.isLimitOrderCompleteTopic,
    TopicClass: LimitOrderCompleteTopic,
  },
  {
    isTopicType: WebNotificationsTopic.isWebNotificationsTopic,
    TopicClass: WebNotificationsTopic,
  },
  {
    isTopicType: HoldersTopic.isHoldersTopic,
    TopicClass: HoldersTopic,
  },
  {
    isTopicType: TransferTopic.isTransferTopic,
    TopicClass: TransferTopic,
  },
  {
    isTopicType: WithdrawalTopic.isWithdrawalTopic,
    TopicClass: WithdrawalTopic,
  },
  {
    isTopicType: UserSettingsTopic.isUserSettingsTopic,
    TopicClass: UserSettingsTopic,
  },
];

/**
 * A factory function to create the correct topic instance based on the topic
 * name.
 */
export const createTopic = (args: {
  ws: WebSocket;
  topic: TopicName;
  store: StoreApi<GlobalStore>;
  persistentStore: StoreApi<PersistentGlobalStore>;
  queryClient: QueryClient;
  args?: Record<string, unknown>;
  keepAliveInterval?: number;
}): TopicInstance => {
  for (const config of TOPIC_CONFIGS) {
    if (config.isTopicType(args.topic)) {
      return new config.TopicClass(args);
    }
  }

  // This should only happen if you forgot to add a new topic to the config
  throw new Error(`Unknown topic: ${args.topic}`);
};

