     1#!/usr/bin/env bun
     2/**
     3 * PAI Skill Validator
     4 *
     5 * Validates that all skills meet PAI structure requirements:
     6 * - Valid YAML frontmatter with name and description
     7 * - Description contains "USE WHEN" keyword
     8 * - Description is single-line and < 1024 chars
     9 * - Name is TitleCase
    10 * - Has Workflow Routing section
    11 * - Has Examples section
    12 * - Referenced workflow files exist
    13 * - tools/ directory exists
    14 * - No backups/ directory inside skill
    15 *
    16 * Run: bun .claude/Hooks/validate-skills.ts
    17 */
    18
    19import { existsSync, readFileSync, readdirSync, statSync } from 'fs';
    20import { join, basename } from 'path';
    21import { PAI_DIR, SKILLS_DIR } from './lib/pai-paths';
    22
    23interface ValidationResult {
    24  skill: string;
    25  status: 'pass' | 'fail' | 'warn';
    26  errors: string[];
    27  warnings: string[];
    28}
    29
    30const results: ValidationResult[] = [];
    31
    32// ANSI color codes
    33const GREEN = '\x1b[32m';
    34const RED = '\x1b[31m';
    35const YELLOW = '\x1b[33m';
    36const RESET = '\x1b[0m';
    37const BOLD = '\x1b[1m';
    38const DIM = '\x1b[2m';
    39
    40function isTitleCase(str: string): boolean {
    41  return /^[A-Z][a-zA-Z0-9]*$/.test(str);
    42}
    43
    44function parseYamlFrontmatter(content: string): { name?: string; description?: string } | null {
    45  const match = content.match(/^---\n([\s\S]*?)\n---/);
    46  if (!match) return null;
    47
    48  const yaml = match[1];
    49  const result: { name?: string; description?: string } = {};
    50
    51  // Parse name (simple line match)
    52  const nameMatch = yaml.match(/^name:\s*(.+)$/m);
    53  if (nameMatch) {
    54    result.name = nameMatch[1].trim();
    55  }
    56
    57  // Parse description - must be single line
    58  const descMatch = yaml.match(/^description:\s*(.+)$/m);
    59  if (descMatch) {
    60    result.description = descMatch[1].trim();
    61  }
    62
    63  return result;
    64}
    65
    66function extractWorkflowRefs(content: string): string[] {
    67  const refs: string[] = [];
    68
    69  // Match workflow routing table rows: | **Name** | "trigger" | `workflows/File.md` |
    70  const tablePattern = /\|\s*\*?\*?(\w+)\*?\*?\s*\|[^|]+\|\s*`?workflows\/([^`|\s]+)`?\s*\|/g;
    71  let match;
    72  while ((match = tablePattern.exec(content)) !== null) {
    73    refs.push(match[2]);
    74  }
    75
    76  return refs;
    77}
    78
    79function validateSkill(skillDir: string): ValidationResult {
    80  const skillName = basename(skillDir);
    81  const errors: string[] = [];
    82  const warnings: string[] = [];
    83
    84  const skillMd = join(skillDir, 'SKILL.md');
    85
    86  // Check SKILL.md exists
    87  if (!existsSync(skillMd)) {
    88    errors.push('Missing SKILL.md file');
    89    return { skill: skillName, status: 'fail', errors, warnings };
    90  }
    91
    92  const content = readFileSync(skillMd, 'utf-8');
    93
    94  // Parse YAML frontmatter
    95  const frontmatter = parseYamlFrontmatter(content);
    96  if (!frontmatter) {
    97    errors.push('Missing or invalid YAML frontmatter (must start with ---)');
    98  } else {
    99    // Validate name
   100    if (!frontmatter.name) {
   101      errors.push('Missing "name:" in frontmatter');
   102    } else if (!isTitleCase(frontmatter.name)) {
   103      errors.push(`Name "${frontmatter.name}" is not TitleCase`);
   104    }
   105
   106    // Validate description
   107    if (!frontmatter.description) {
   108      errors.push('Missing "description:" in frontmatter');
   109    } else {
   110      if (frontmatter.description.length > 1024) {
   111        errors.push(`Description exceeds 1024 chars (${frontmatter.description.length})`);
   112      }
   113      if (!frontmatter.description.includes('USE WHEN')) {
   114        errors.push('Description missing "USE WHEN" keyword (required for Claude Code parsing)');
   115      }
   116      if (frontmatter.description.includes('\n')) {
   117        errors.push('Description must be single-line (no newlines)');
   118      }
   119    }
   120  }
   121
   122  // Check for Workflow Routing section
   123  if (!content.includes('## Workflow Routing')) {
   124    warnings.push('Missing "## Workflow Routing" section');
   125  }
   126
   127  // Check for Examples section
   128  if (!content.includes('## Examples')) {
   129    errors.push('Missing "## Examples" section (critical for skill activation)');
   130  } else {
   131    // Count examples
   132    const exampleCount = (content.match(/\*\*Example \d+:/g) || []).length;
   133    if (exampleCount < 2) {
   134      warnings.push(`Only ${exampleCount} example(s) found (recommend 2-3)`);
   135    }
   136  }
   137
   138  // Check tools/ directory exists
   139  const toolsDir = join(skillDir, 'tools');
   140  if (!existsSync(toolsDir)) {
   141    warnings.push('Missing tools/ directory');
   142  }
   143
   144  // Check for forbidden backups/ directory
   145  const backupsDir = join(skillDir, 'backups');
   146  if (existsSync(backupsDir)) {
   147    errors.push('Contains backups/ directory (violates PAI convention)');
   148  }
   149
   150  // Check workflow references
   151  const workflowsDir = join(skillDir, 'workflows');
   152  if (existsSync(workflowsDir)) {
   153    const workflowRefs = extractWorkflowRefs(content);
   154    for (const ref of workflowRefs) {
   155      const workflowPath = join(workflowsDir, ref);
   156      if (!existsSync(workflowPath)) {
   157        errors.push(`Referenced workflow not found: workflows/${ref}`);
   158      }
   159    }
   160  }
   161
   162  // Determine overall status
   163  let status: 'pass' | 'fail' | 'warn' = 'pass';
   164  if (errors.length > 0) {
   165    status = 'fail';
   166  } else if (warnings.length > 0) {
   167    status = 'warn';
   168  }
   169
   170  return { skill: skillName, status, errors, warnings };
   171}
   172
   173// Main execution
   174console.log(`\n${BOLD}PAI Skill Validator${RESET}\n`);
   175console.log('='.repeat(60));
   176
   177// Find all skill directories
   178const skillDirs = readdirSync(SKILLS_DIR)
   179  .filter(f => {
