    10function generateTabTitle(prompt: string, completedLine?: string): string {
    11  // If we have a completed line, try to use it for a better summary
    12  if (completedLine) {
    13    const cleanCompleted = completedLine
    14      .replace(/\*+/g, '')
    15      .replace(/\[.*?\]/g, '')
    16      .replace(/\s*COMPLETED:\s*/gi, '')
    17      .trim();
