    19    // Extract meaningful words from the completed line
    20    const completedWords = cleanCompleted.split(/\s+/)
    21      .filter(word => word.length > 2 &&
    22        !['the', 'and', 'but', 'for', 'are', 'with', 'his', 'her', 'this', 'that', 'you', 'can', 'will', 'have', 'been', 'your', 'from', 'they', 'were', 'said', 'what', 'them', 'just', 'told', 'how', 'does', 'into', 'about', 'completed'].includes(word.toLowerCase()))
    23      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase());
    24
    25    if (completedWords.length >= 2) {
    26      // Build a 4-word summary from completed line
    27      const summary = completedWords.slice(0, 4);
    28      while (summary.length < 4) {
    29        summary.push('Done');
    30      }
    31      return summary.slice(0, 4).join(' ');
    32    }
    33  }
    34
    35  // Fall back to parsing the prompt
    36  const cleanPrompt = prompt.replace(/[^\w\s]/g, ' ').trim();
    37  const words = cleanPrompt.split(/\s+/).filter(word =>
    38    word.length > 2 &&
    39    !['the', 'and', 'but', 'for', 'are', 'with', 'his', 'her', 'this', 'that', 'you', 'can', 'will', 'have', 'been', 'your', 'from', 'they', 'were', 'said', 'what', 'them', 'just', 'told', 'how', 'does', 'into', 'about'].includes(word.toLowerCase())
    40  );
    41
    42  const lowerPrompt = prompt.toLowerCase();
    43
    44  // Find action verb if present
    45  const actionVerbs = ['test', 'rename', 'fix', 'debug', 'research', 'write', 'create', 'make', 'build', 'implement', 'analyze', 'review', 'update', 'modify', 'generate', 'develop', 'design', 'deploy', 'configure', 'setup', 'install', 'remove', 'delete', 'add', 'check', 'verify', 'validate', 'optimize', 'refactor', 'enhance', 'improve', 'send', 'email', 'help', 'updated', 'fixed', 'created', 'built', 'added'];
    46
    47  let titleWords = [];
    48
    49  // Check for action verb
    50  for (const verb of actionVerbs) {
    51    if (lowerPrompt.includes(verb)) {
    52      // Convert to past tense for summary
    53      let pastTense = verb;
    54      if (verb === 'write') pastTense = 'Wrote';
    55      else if (verb === 'make') pastTense = 'Made';
    56      else if (verb === 'send') pastTense = 'Sent';
    57      else if (verb.endsWith('e')) pastTense = verb.charAt(0).toUpperCase() + verb.slice(1, -1) + 'ed';
    58      else pastTense = verb.charAt(0).toUpperCase() + verb.slice(1) + 'ed';
    59
    60      titleWords.push(pastTense);
    61      break;
    62    }
    63  }
    64
    65  // Add most meaningful remaining words
    66  const remainingWords = words
    67    .filter(word => !actionVerbs.includes(word.toLowerCase()))
    68    .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase());
    69
    70  // Fill up to 4 words total
    71  for (const word of remainingWords) {
    72    if (titleWords.length < 4) {
    73      titleWords.push(word);
    74    } else {
    75      break;
    76    }
    77  }
    78
    79  // If we don't have enough words, add generic ones
    80  if (titleWords.length === 0) {
    81    titleWords.push('Completed');
    82  }
    83  if (titleWords.length === 1) {
    84    titleWords.push('Task');
    85  }
    86  if (titleWords.length === 2) {
    87    titleWords.push('Successfully');
    88  }
    89  if (titleWords.length === 3) {
    90    titleWords.push('Done');
    91  }
    92
    93  return titleWords.slice(0, 4).join(' ');
    94}
