   108    process.stderr.write(`\x1b]2;${title}\x07`);  // Window title
   109    process.stderr.write(`\x1b]0;${title}\x07`);  // Icon and window title
   110
   111    // Try OSC 7 for Ghostty tab titles (some terminals use this)
   112    process.stderr.write(`\x1b]7;${title}\x07`);
   113
   114    // Also try the standard xterm way with ST terminator
   115    process.stderr.write(`\x1b]2;${title}\x1b\\`);
