   118    process.stderr.write(`\x1b]0;${title}\x07`);
   119    process.stderr.write(`\x1b]2;${title}\x07`);
   120    process.stderr.write(`\x1b]30;${title}\x07`);  // Kitty-specific
   121  } else {
