     1#!/usr/bin/env bun
     2/**
     3 * Capture All Events Hook
     4 * Captures ALL Claude Code hook events (not just tools) to JSONL
     5 * This replaces the Python send_event.py hook
     6 * Enhanced with agent instance metadata extraction
     7 */
     8
     9import { readFileSync, appendFileSync, mkdirSync, existsSync, writeFileSync } from 'fs';
    10import { join } from 'path';
    11import { PAI_DIR, HISTORY_DIR } from './lib/pai-paths';
    12import { enrichEventWithAgentMetadata, isAgentSpawningCall } from './lib/metadata-extraction';
    13
    14interface HookEvent {
    15  source_app: string;
    16  session_id: string;
    17  hook_event_type: string;
    18  payload: Record<string, any>;
    19  timestamp: number;
    20  timestamp_pst: string;
    21}
    22
    23// Get PST timestamp
    24function getPSTTimestamp(): string {
    25  const date = new Date();
    26  const pstDate = new Date(date.toLocaleString('en-US', { timeZone: process.env.TIME_ZONE || 'America/Los_Angeles' }));
    27
    28  const year = pstDate.getFullYear();
    29  const month = String(pstDate.getMonth() + 1).padStart(2, '0');
    30  const day = String(pstDate.getDate()).padStart(2, '0');
    31  const hours = String(pstDate.getHours()).padStart(2, '0');
    32  const minutes = String(pstDate.getMinutes()).padStart(2, '0');
    33  const seconds = String(pstDate.getSeconds()).padStart(2, '0');
    34
    35  return `${year}-${month}-${day} ${hours}:${minutes}:${seconds} PST`;
    36}
    37
    38// Get current events file path
    39function getEventsFilePath(): string {
