import {
  Outlet,
  createRootRouteWithContext,
  redirect,
} from "@tanstack/react-router";
import { QueryClient } from "@tanstack/react-query";
// import { TanStackRouterDevtools } from "@tanstack/react-router-devtools";
import { Providers } from "@/providers/Providers";
import {
  checkAuthentication,
  type CheckAuthenticationProps,
} from "@/auth/checkAuthentication";
import { isPasskeyApiSupported } from "@/utils/passkey";

type RouterContext = {
  queryClient: QueryClient;
};

const rootBeforeLoad = async (args: CheckAuthenticationProps) => {
  const { location } = args;

  const isUnsupportedPasskeyPage = location.pathname.startsWith(
    "/unsupported-passkey"
  );

  if (!isUnsupportedPasskeyPage && !isPasskeyApiSupported()) {
    throw redirect({
      to: "/unsupported-passkey",
      replace: true,
      state: {
        redirectUrl:
          typeof window !== "undefined"
            ? window.location.href
            : location.pathname + location.search + location.hash,
      },
    });
  }

  if (isUnsupportedPasskeyPage) {
    // don't check auth on unsupported page
    return;
  }

  return checkAuthentication(args);
};

export const Route = createRootRouteWithContext<RouterContext>()({
  beforeLoad: rootBeforeLoad,
  component: () => (
    <Providers>
      <Outlet />
      {/* <TanStackRouterDevtools position="bottom-right" /> */}
    </Providers>
  ),
});

