///////////////////
// ENV VARIABLES
///////////////////
export const AUTHBOT_URL = import.meta.env.VITE_AUTHBOT_URL;

// API URLs
export const IMG_PROXY_URL = import.meta.env.VITE_GUILD_NAVIGATOR_URL;
export const BONKBOT_IMAGES_URL = import.meta.env.VITE_BONKBOT_IMAGES_URL;
export const METADATA_API_URL = import.meta.env.VITE_METADATA_API_URL;
export const STREAMFLOW_DASHBOARD_BASE_URL =
  import.meta.env.VITE_STREAMFLOW_DASHBOARD_BASE_URL ||
  "https://app.streamflow.finance/token-dashboard/solana/mainnet";
export const JUPITER_DASHBOARD_BASE_URL =
  import.meta.env.VITE_JUPITER_DASHBOARD_BASE_URL ||
  "https://lock.jup.ag/escrow";
export const APP_ENV = import.meta.env.VITE_APP_ENV;

// External services
export const BUBBLE_MAP_BASE_URL = import.meta.env.VITE_BUBBLE_MAP_BASE_URL;
export const BUBBLE_MAP_EMBED_ID = import.meta.env.VITE_BUBBLE_MAP_EMBED_ID;

export const IS_PRODUCTION_ENV = APP_ENV === "prod";
export const IS_STAGING_ENV = APP_ENV === "staging";

export const IS_POSTHOG_ENABLED = IS_PRODUCTION_ENV || IS_STAGING_ENV;

export const DISABLE_LIMIT_ORDERS = false;

export const SKIP_API_PROXY = import.meta.env.VITE_SKIP_API_PROXY === "true";
export const IS_LOCAL_ENV = APP_ENV === "local";

///////////////////////
// API & SIGNER config
///////////////////////

// Custom API environment headers (only for non-prod), check cloudflare previews-redirects worker
export const CUSTOM_WEB_API = !IS_PRODUCTION_ENV
  ? import.meta.env.VITE_CUSTOM_WEB_API
  : undefined;
export const CUSTOM_DATA_API = !IS_PRODUCTION_ENV
  ? import.meta.env.VITE_CUSTOM_DATA_API
  : undefined;

export const SIGNER_URL =
  !IS_PRODUCTION_ENV && window.CUSTOM_SIGNER_URL
    ? window.CUSTOM_SIGNER_URL
    : import.meta.env.VITE_SIGNER_URL;

const BOT_NAME =
  !IS_PRODUCTION_ENV && window.CUSTOM_BOT_NAME
    ? window.CUSTOM_BOT_NAME
    : import.meta.env.VITE_BOT_NAME;

export const SIGNER_URL_WITH_BOT_NAME = SIGNER_URL + "/" + BOT_NAME;

export const WEBSOCKET_URL = SKIP_API_PROXY
  ? import.meta.env.VITE_DATA_API_URL
  : IS_LOCAL_ENV
    ? `wss://${window.location.hostname}:${window.location.port || 3000}/data`
    : `wss://${window.location.hostname}/data`;

// Socket.IO Server URL
export const SENTINEL_SOCKET_IO_URL = import.meta.env
  .VITE_SENTINEL_SOCKET_IO_URL;

// PVP URLS
export const PVP_API_URL = import.meta.env.VITE_PVP_API_URL;

///////////////////////

// required only on localhost, as our domains are whitelisted
export const PINATA_GATEWAY_TOKEN = import.meta.env.VITE_PINATA_GATEWAY_TOKEN;
export const PINATA_GATEWAY_URL =
  IS_PRODUCTION_ENV || IS_STAGING_ENV
    ? "https://images.telemetry-cdn.io"
    : "https://images-beta.telemetry-cdn.io";

// SENTRY CONFIG
export const SENTRY_DSN = import.meta.env.VITE_SENTRY_DSN;
export const ENABLE_REACT_SCAN =
  import.meta.env.VITE_ENABLE_REACT_SCAN === "true";
export const ENABLE_ERUDA = import.meta.env.VITE_ENABLE_ERUDA === "true";

///////////////////
// UI related config
///////////////////
export const DEFAULT_ROUTE_DESKTOP = "/dashboard";
export const DEFAULT_ROUTE_MOBILE = "/x-ray";

export const MIN_SLIPPAGE_WARNING = 50;
export const MIN_PRIORITY_FEE_WARNING = 5;

export const MAXIMUM_AUTO_STRATS = 3;
export const MAXIMUM_AUTO_STRAT_ORDERS = 4;
export const FALLBACK_MCAP_TRIGGER_VALUE = "1";
export const MAX_MCAP = 1e14;
export const MAX_RANGE_FILTER = 1e14;

export const TOKEN_EVENTS_FETCH_LIMIT = 10000;

export const IS_DEVELOP = import.meta.env.DEV === true;

// Enable sentry on staging, prod, and any preview urls
export const IS_SENTRY_ENABLED =
  IS_PRODUCTION_ENV ||
  IS_STAGING_ENV ||
  window.location.origin.includes("pr.bonkbot.io") ||
  window.location.origin.includes("guild-navigator.bonkbot.io");

export const DISCORD_URL = "https://discord.gg/qy5ukGRs5M";
export const X_URL = "https://x.com/bonkbot_io";
export const TG_SUPPORT_URL = "https://t.me/BONKbotChat";
export const DOCS_URL = "https://docs.bonkbot.io";
export const CHART_BRANDING = "TELEMETRY";
export const TOS_URL = "https://telemetry.io/terms-of-use";
export const PRIVACY_POLICY_URL = "https://telemetry.io/privacy-policy";
export const MIN_SWAP_DURATION_BEFORE_SHOWING_DESCRIPTION = 1000 * 5; // 5sec
export const TRANSACTION_FAILURE_TIMESTAMP_THRESHOLD = 1000 * 60 * 5; // 5 minutes

///////////////////
// Refetching config
///////////////////
export const RQUERY_REFETCH_INTERVAL = 10000; // 10sec
export const RQUERY_EXPECTED_PROFIT_REFETCH_INTERVAL = 2500; // 5sec
export const RQUERY_POSITIONS_REFETCH_INTERVAL = 5000; // 5secs
export const RQUERY_STALE_TIME_INFINITY = Infinity;
export const RQUERY_PNL_V2_REFETCH_INTERVAL = 180000; // 3 minutes

export const RQUERY_TOTAL_BALANCE_REFETCH_INTERVAL = 5000;
export const RQUERY_ACTIVE_WALLETS_TABLE_REFETCH_INTERVAL = 5000;

// UI config
export const TABLE_ROW_HEIGHT_SMALL = 36;

// websocket per env
export const WS_TOPIC_ENV =
  IS_PRODUCTION_ENV || IS_STAGING_ENV ? "prod" : "beta";

export const TRADINGVIEW_IFRAME_ID = "tradingview-iframe";

// Withdrawal fees (in lamports)
export const WITHDRAWAL_BASE_FEE_IN_LAMPORTS = 5000;
export const WITHDRAWAL_PRIORITY_FEE_IN_LAMPORTS = 200;
export const WITHDRAWAL_TOTAL_FEE_IN_LAMPORTS =
  WITHDRAWAL_BASE_FEE_IN_LAMPORTS + WITHDRAWAL_PRIORITY_FEE_IN_LAMPORTS;

export const DEFAULT_LOGIN_PAGE_URL = "/sign-up";

