import React from "react";

import { PosthogProvider } from "./PosthogProvider/PosthogProvider";
import { OrderCancelModalProvider } from "@/providers/OrderCancelModalProvider/OrderCancelModalProvider";
import { RealtimeDataProvider } from "./RealtimeDataProvider/RealtimeDataProvider";

export const Providers = ({ children }: { children: React.ReactNode }) => {
  return (
    <PosthogProvider>
      <OrderCancelModalProvider>{children}</OrderCancelModalProvider>
      <RealtimeDataProvider />
    </PosthogProvider>
  );
