import type { SubStore } from "@/store/types";
import type { DiscoveryStore } from "./types";
import type { GlobalStore } from "../../types";

export const getDiscoveryStore: SubStore<
  DiscoveryStore,
  GlobalStore
> = set => ({
  isTrendingDataLoading: true,
  isFilterSheetOpen: false,
  editingPresetId: undefined,
  draftPresetName: undefined,
  setIsTrendingDataLoading: isLoading =>
    set(
      state => {
        state.discovery.isTrendingDataLoading = isLoading;
      },
      false,
      { type: "discovery/setIsTrendingDataLoading", data: isLoading }
    ),
  setIsFilterSheetOpen: isOpen =>
    set(
      state => {
        state.discovery.isFilterSheetOpen = isOpen;
        // Clear editing state when closing the sheet
        if (!isOpen && state.discovery.editingPresetId) {
          state.discovery.editingPresetId = undefined;
        }
      },
      false,
      { type: "discovery/setIsFilterSheetOpen", data: isOpen }
    ),
  setEditingPresetId: id =>
    set(
      state => {
        state.discovery.editingPresetId = id;
      },
      false,
      { type: "discovery/setEditingPresetId", data: id }
    ),
  setDraftPresetName: name =>
    set(
      state => {
        state.discovery.draftPresetName = name;
      },
      false,
      { type: "discovery/setDraftPresetName", data: name }
    ),
});

