import * as Sentry from "@sentry/react";
import { StrictMode, useEffect } from "react";
import { createRoot } from "react-dom/client";
import {
  APP_ENV,
  ENABLE_ERUDA,
  IS_SENTRY_ENABLED,
  SENTRY_DSN,
} from "./config.ts";
import "swiper/css";
import "swiper/css/pagination";
import { RouterProvider } from "@tanstack/react-router";
import pkg from "../package.json";
import { beforeSend } from "@/sentry/beforeSend.ts";
import { init } from "emoji-mart";
import data from "@emoji-mart/data/sets/14/apple.json";
import {
  queryClient,
  ReactQueryProvider,
} from "@/providers/ReactQueryProvider/ReactQueryProvider.tsx";
import { Toaster } from "@/components/sonner.tsx";
import { useDetectLocale } from "@/ui/i18n/useDetectLocale.ts";
import { I18nProvider } from "@lingui/react";
import { i18n } from "@lingui/core";

import { applyGoogleTranslateFix } from "@/utils/google-translate-patch.ts";
import { getSessionId } from "@/utils/session-id";
// import { useIdleDismount } from "@/hooks/useIdleDismount.ts";
// import { Loader2 } from "lucide-react";
import { router } from "@/routes/router.tsx";
import { handleWindowNetworkConnection } from "@/websocket/connection/handleWindowNetworkConnection.ts";
import { subscribeToMarketDataForActiveMints } from "@/websocket/subscribers/activeMintSubscription.ts";
// import { useBlankPageDetection } from "@/hooks/useBlankPageDetection.ts";
import { natsWebsocketReady } from "@/websocket/nats/connectNatsWebsocket.tsx";
// import { isStaleAssetsError } from "@/utils/errors/stale-assets";
import { useVersionCheck } from "@/hooks/useVersionCheck";

applyGoogleTranslateFix();

const Providers = ({ children }: { children: React.ReactNode }) => {
  const { currentLocale } = useDetectLocale();
  return (
    <I18nProvider
      key={currentLocale}
      i18n={i18n}
    >
      <ReactQueryProvider>{children}</ReactQueryProvider>
    </I18nProvider>
  );
};

export const App = () => {
  const sessionId = getSessionId();

  // useBlankPageDetection(sessionId);
  useVersionCheck();

  useEffect(() => {
    init({ data });
  }, []);

  useEffect(() => {
    const cleanup = handleWindowNetworkConnection();
    return cleanup;
  }, []);

  if (IS_SENTRY_ENABLED) {
    Sentry.init({
      dsn: SENTRY_DSN,
      release: __RELEASE_NAME__,
      environment: APP_ENV || "local",
      sendDefaultPii: true,
      integrations: [
        Sentry.tanstackRouterBrowserTracingIntegration(router),
        Sentry.contextLinesIntegration(),
      ],
      tracesSampleRate: 1.0,
      tracePropagationTargets: [/^\//, /^\/v1/, /^\/data/],
      beforeSend,
      // Ignore method not found https://github.com/getsentry/sentry-javascript/issues/13966 (visible only on chrome android, not causing any app errors)
      /* eslint-disable lingui/no-unlocalized-strings */
      ignoreErrors: [
        "Method not found",
        "ethereum",
        "ethereum is not defined",
        // /^Network ?Error$/i,
        // /^Network request failed$/i,
        // /^Failed to fetch$/i,
        // /^Load failed$/i,
      ],
    });

    Sentry.setTag("session_id", sessionId);
  }

  console.log("Used ENV:", import.meta.env.VITE_APP_ENV);
  console.log("VERSION:", pkg.version);

  useEffect(() => {
    if (ENABLE_ERUDA) {
      import("eruda").then(module => {
        module.default.init();
      });
    }
  }, []);

  return (
    // I18n is used inside toasts, route loaders, and inside App routes
    // themselves, so it must be rendered here
    <>
      <Toaster
        visibleToasts={10}
        gap={6}
      />
      <RouterProvider
        router={router}
        context={{ queryClient }}
      />
    </>
  );
};

// Set up store subscriptions globally
natsWebsocketReady().then(() => {
  subscribeToMarketDataForActiveMints();
});

// const reloadThrottleInMs = 30_000;

// const reloadForStaleAssets = () => {
//   const reloadKey = "bb-missing-asset-last-reload";
//   const lastReloadRaw = sessionStorage.getItem(reloadKey);
//   const lastReload = lastReloadRaw ? Number(lastReloadRaw) : null;
//   if (!lastReload || Date.now() - lastReload > reloadThrottleInMs) {
//     sessionStorage.setItem(reloadKey, Date.now().toString());
//     window.location.reload();
//   }
// };

// const createErrorHandler = <T extends (...args: any[]) => void>(
//   handler: T
// ): T => {
//   return (IS_SENTRY_ENABLED ? Sentry.reactErrorHandler(handler) : handler) as T;
// };

createRoot(document.getElementById("root")!, {
  // onUncaughtError: createErrorHandler((error, errorInfo) => {
  //   if (isStaleAssetsError(error)) {
  //     reloadForStaleAssets();
  //     return;
  //   }
  //   console.warn("Uncaught error:", error, errorInfo.componentStack);
  // }),
  // onCaughtError: createErrorHandler(error => {
  //   if (isStaleAssetsError(error)) {
  //     reloadForStaleAssets();
  //     return;
  //   }
  //   console.warn("Caught error:", error);
  // }),
  // onRecoverableError: createErrorHandler(error => {
  //   if (isStaleAssetsError(error)) {
  //     reloadForStaleAssets();
  //     return;
  //   }
  //   console.warn("Recoverable error:", error);
  // }),
}).render(
  <StrictMode>
    <Providers>
      <App />
    </Providers>
  </StrictMode>
);

