import { createAxiosClient } from "@/api/axios";
import axios, { type AxiosRequestConfig, type AxiosResponse } from "axios";
import {
  SKIP_API_PROXY,
  IS_PRODUCTION_ENV,
  IS_LOCAL_ENV,
  CUSTOM_WEB_API,
  CUSTOM_DATA_API,
  PVP_API_URL,
} from "@/config";

const WEB_API_HEADERS: Record<string, string> =
  !IS_PRODUCTION_ENV && CUSTOM_WEB_API
    ? { "custom-web-api": CUSTOM_WEB_API }
    : {};

const DATA_API_HEADERS: Record<string, string> =
  !IS_PRODUCTION_ENV && CUSTOM_DATA_API
    ? { "custom-data-api": CUSTOM_DATA_API }
    : {};

export const API_BONKBOT_CLIENT = createAxiosClient({
  baseURL: "/v1",
  withAuthInterceptor: true,
  headers: WEB_API_HEADERS,
});

const API_DATA_CLIENT = createAxiosClient({
  baseURL: SKIP_API_PROXY ? import.meta.env.VITE_DATA_API_URL : "/data",
  withAuthInterceptor: true,
  withDataTokenInterceptor: true,
  headers: DATA_API_HEADERS,
});

const API_PVP_CLIENT = createAxiosClient({
  baseURL: PVP_API_URL + "/api",
  withAuthInterceptor: false,
  withDataTokenInterceptor: true,
});

const DEXSCREENER_CLIENT = axios.create({
  baseURL: "https://api.dexscreener.com/latest/dex",
});

const SCRAPER_CLIENT = axios.create({
  baseURL: IS_LOCAL_ENV
    ? `${import.meta.env.VITE_GUILD_NAVIGATOR_URL}/x-parser`
    : "/x-parser",
});

const API_LIST = {
  apiBonkbot: API_BONKBOT_CLIENT,
  apiData: API_DATA_CLIENT,
  apiPvp: API_PVP_CLIENT,
  dexscreener: DEXSCREENER_CLIENT,
  scraper: SCRAPER_CLIENT,
} as const;

type HTTPMethod = "GET" | "POST" | "PUT" | "DELETE" | "PATCH";

type RequestParams<T = undefined> = {
  method: HTTPMethod;
  path: string;
  params?: Record<string, unknown>;
  config?: AxiosRequestConfig;
  client: keyof typeof API_LIST;
  body?: Partial<T>;
  useUrlEncoded?: true;
  skipAuthRefresh?: boolean;
  signal?: AbortSignal;
};

export const request = async <RequestType, ResponseType>(
  params: RequestParams<RequestType>
): Promise<ResponseType> => {
  try {
    const {
      client,
      method,
      path,
      body,
      params: queryParams,
      config,
      useUrlEncoded,
      skipAuthRefresh,
      signal,
    } = params;

    // Convert POST request body to x-www-form-urlencoded format
    let requestHeaders = config?.headers || {};

    let urlEncodedBody = "";

    if (useUrlEncoded && body && typeof body === "object") {
      const formData = new URLSearchParams();

      // Convert object to URLSearchParams
      Object.entries(body).forEach(([key, value]) => {
        if (value !== undefined && value !== null) {
          formData.append(key, String(value));
        }
      });

      urlEncodedBody = formData.toString();
      requestHeaders = {
        ...requestHeaders,
        "Content-Type": "application/x-www-form-urlencoded",
      };
    }

    const axiosConfig: AxiosRequestConfig = {
      ...config,
      method,
      url: path,
      data: urlEncodedBody || body,
      params: queryParams,
      headers: requestHeaders,
      skipAuthRefresh,
      signal,
    };

    const response: AxiosResponse<ResponseType> =
      await API_LIST[client].request(axiosConfig);
    return response.data;
  } catch (error) {
    console.error("API response error", error);
    if (axios.isAxiosError(error)) {
      throw error.response?.data || error;
    }

    throw error;
  }
};

export const createQueryFn = <RequestType, ResponseType>(
  requestParams: Omit<RequestParams<RequestType>, "signal">
) => {
  return ({ signal: _signal }: { signal?: AbortSignal }) =>
    request<RequestType, ResponseType>({
      ...requestParams,
      // signal, // temporarily commented out
    });
};

// A custom mutator for Orval so we can share our Axios client with the OpenAPI
// client too. See orval.config.ts for more details
export const orvalApiBonkbotInstance = <T>(
  config: AxiosRequestConfig
): Promise<T> => {
  const promise = API_BONKBOT_CLIENT({
    baseURL: "/",
    ...config,
  }).then(({ data }) => data);

  return promise;
};

