import { router } from "@/routes/router";
import { useGlobalStore } from "@/store/memoryStore/useGlobalStore";
import { natsWebsocket } from "@/websocket/nats/natsWebsocketInstance";
import { captureEvent } from "@/hooks/useAnalytics/analytics";
import { User } from "@/api/auth/types";
import { queryKeys } from "@/api/queryKeys";
import { WS_TOPIC_ENV } from "@/config";
import { queryClient } from "@/providers/ReactQueryProvider/ReactQueryProvider";
import { disconnectWebsockets } from "@/utils/websocket-utils";

export function handleWindowNetworkConnection(): () => void {
  const getTopicInfo = () => {
    const user = queryClient.getQueryData<User>([queryKeys.userMe]);
    return {
      topic: user ? `swap.${user.telemetryId}` : undefined,
      topicV2: user ? `${WS_TOPIC_ENV}.swap.${user.telemetryId}` : undefined,
    };
  };

  const isSwapTopic = (topic: string): boolean => {
    return topic.startsWith(`${WS_TOPIC_ENV}.swap.`);
  };

  const onlineHandler = () => {
    const activeSwapTopics = Array.from(natsWebsocket.topics.keys()).filter(
      topic => isSwapTopic(topic)
    );

    if (activeSwapTopics.length > 0) {
      captureEvent({
        event: {
          page: "swap_debug",
          component: "network",
          action: "browser_online",
          metadata: {
            ...getTopicInfo(),
          },
        },
      });
    }

    useGlobalStore.getState().connectionManager.setOffline(false);
    router.invalidate();
  };

  const offlineHandler = () => {
    const activeSwapTopics = Array.from(natsWebsocket.topics.keys()).filter(
      topic => isSwapTopic(topic)
    );

    if (activeSwapTopics.length > 0) {
      captureEvent({
        event: {
          page: "swap_debug",
          component: "network",
          action: "browser_offline",
          metadata: {
            ...getTopicInfo(),
          },
        },
      });
    }

    useGlobalStore.getState().connectionManager.setOffline(true);
    disconnectWebsockets();
  };

  window.addEventListener("online", onlineHandler);
  window.addEventListener("offline", offlineHandler);

  return () => {
    window.removeEventListener("online", onlineHandler);
    window.removeEventListener("offline", offlineHandler);
  };
}

