import { ParsedLocation, redirect } from "@tanstack/react-router";
import { QueryClient } from "@tanstack/react-query";
import * as Sentry from "@sentry/react";
import { useAuthStore } from "@/store/auth-store/auth-store";
import { formatAddress } from "@/lib/formatters";

export type LastAuthUser = {
  telemetryId: number;
  authProvider: "solana_wallet" | "email" | "telegram";
  authProviderUserId: string;
};

import {
  getAuthTokenExpiry,
  getDataTokenExpiry,
  getRefreshTokenExpiry,
  isAuthTokenExpired,
  isDataTokenExpired,
  isRefreshTokenExpired,
} from "@/utils/auth-tokens";
import { t } from "@lingui/core/macro";
import { DEFAULT_LOGIN_PAGE_URL } from "@/config";

/**
 * Checks if the current location is a login-related page
 * @param pathname - The pathname to check
 * @returns true if the pathname represents a login page
 */
export const isLoginPage = (pathname: string): boolean => {
  return (
    pathname.startsWith("/login") ||
    pathname.startsWith("/@") ||
    pathname === "/sign-up" ||
    pathname.startsWith("/auth/verify-email")
    // ||
    // pathname.startsWith("/auth/authbot") ||
    // pathname.startsWith("/auth/authorize")
  );
};

export const isAuthTokenExchangePage = (pathname: string): boolean => {
  return (
    pathname.startsWith("/auth/authbot") ||
    pathname.startsWith("/auth/authorize")
  );
};

export const postLoginRedirect = (): ReturnType<typeof redirect> => {
  const postLoginRedirect = localStorage.getItem("postLoginRedirect");
  if (postLoginRedirect) {
    localStorage.removeItem("postLoginRedirect");
    return redirect({ to: postLoginRedirect });
  } else {
    console.log("redirecting to /");
    return redirect({ to: "/" });
  }
};

export const redirectToLoginPage = (location: ParsedLocation) => {
  if (!isLoginPage(location.pathname)) {
    localStorage.setItem("postLoginRedirect", location.pathname);
    throw redirect({ to: DEFAULT_LOGIN_PAGE_URL, replace: true });
  }
};

/**
 * Handles authentication and redirect based on passkey status
 * @param queryClient - React Query client instance
 * @param location - Current location object
 * @returns Redirect based on passkey status
 */
export const handleAuthenticatedUser = async (
  _queryClient: QueryClient,
  location: ParsedLocation
) => {
  try {
    useAuthStore.getState().authenticate();

    // If we're on a login page, use postLoginRedirect
    if (isLoginPage(location.pathname)) {
      return postLoginRedirect();
    }

    // For other pages, do nothing (user is already authenticated)
  } catch (error) {
    handleRefreshTokenFailure(error, location);
  }
};

/**
 * Logs authentication state for debugging
 */
export const logAuthState = () => {
  console.log("Auth: authTokenExpiry", getAuthTokenExpiry());
  console.log("Auth: refreshTokenExpiry", getRefreshTokenExpiry());
  console.log("Auth: dataTokenExpiry", getDataTokenExpiry());
  console.log("Auth: refreshTokenExpired", isRefreshTokenExpired());
  console.log("Auth: authTokenExpired", isAuthTokenExpired());
  console.log("Auth: dataTokenExpired", isDataTokenExpired());
};

/**
 * Handles refresh token failure with Sentry logging
 * @param error - The error that occurred
 * @param location - Current location object
 */
export const handleRefreshTokenFailure = (
  error: unknown,
  location: ParsedLocation
) => {
  Sentry.captureException(error, {
    tags: {
      location: "root_route_beforeLoad",
      error_type: "refresh_token_failed",
      description:
        // eslint-disable-next-line lingui/no-unlocalized-strings
        "Refresh token failed despite being valid - this should not happen",
    },
    extra: {
      authExpiresAt: getAuthTokenExpiry()
        ? Number.parseInt(getAuthTokenExpiry() || "", 10)
        : null,
      refreshExpiresAt: getRefreshTokenExpiry()
        ? Number.parseInt(getRefreshTokenExpiry() || "", 10)
        : null,
      dataExpiresAt: getDataTokenExpiry()
        ? Number.parseInt(getDataTokenExpiry() || "", 10)
        : null,
      currentTime: Date.now(),
      pathname: location.pathname,
    },
  });

  console.error(
    "Auth: Redirecting to login, calling refresh token failed",
    error
  );

  useAuthStore.getState().unauthenticate();
  redirectToLoginPage(location);
};

// ---- Last Auth User helpers (sync localStorage, no debounce) ----

const LAST_AUTH_USER_STORAGE_KEY = "wt:lastAuthUser";

export const setLastAuthUser = (data: LastAuthUser | null) => {
  try {
    if (!data) {
      localStorage.removeItem(LAST_AUTH_USER_STORAGE_KEY);
      return;
    }
    localStorage.setItem(LAST_AUTH_USER_STORAGE_KEY, JSON.stringify(data));
  } catch (e) {
    console.error("Failed to persist last auth user", e);
  }
};

export const getLastAuthUser = (): LastAuthUser | null => {
  try {
    const raw = localStorage.getItem(LAST_AUTH_USER_STORAGE_KEY);
    if (!raw) return null;
    return JSON.parse(raw) as LastAuthUser;
  } catch (e) {
    console.error("Failed to read last auth user", e);
    return null;
  }
};

export const clearLastAuthUser = () => {
  try {
    localStorage.removeItem(LAST_AUTH_USER_STORAGE_KEY);
  } catch (e) {
    console.error("Failed to clear last auth user", e);
  }
};

// Mask email like y**r@*****il.com
export const maskEmail = (email: string): string => {
  const [local, domain] = email.split("@");
  if (!domain) return email;
  const localMasked = `${local[0] ?? ""}${local.length > 2 ? "**" : "*"}${
    local[local.length - 1] ?? ""
  }`;
  const [domainName, ...tldParts] = domain.split(".");
  const tld = tldParts.join(".");
  const maskedDomain = `${"*".repeat(Math.max(0, domainName.length - 2))}${domainName.slice(-2)}`;
  return `${localMasked}@${maskedDomain}${tld ? `.${tld}` : ""}`;
};

export const formatUsernameForSigner = (
  authProvider: "solana_wallet" | "email" | "telegram",
  authProviderUserId: string,
  telemetryId?: number | string
): string => {
  if (!authProviderUserId || authProviderUserId.trim() === "") {
    if (telemetryId) {
      return String(telemetryId);
    }
    return t`Guest`;
  }

  if (authProvider === "solana_wallet") {
    return formatAddress(authProviderUserId, 4, 4);
  }

  return authProviderUserId;
};

