import { natsWebsocket } from "./natsWebsocketInstance";
import { withRetry } from "@/utils/withRetry";

export async function connectNatsWebsocket() {
  return withRetry({
    callback: async () => {
      await natsWebsocket.connect();
      return natsWebsocket.ready();
    },
    onRetry: (error, attempt) => {
      console.warn(
        `NATS WebSocket connection attempt ${attempt} failed:`,
        error.message
      );
      natsWebsocket.destroy();
    },
  });
}

export async function natsWebsocketReady() {
  await natsWebsocket.ready();
}

