import {
  BaseErrorTransaction,
  BaseInitialTransaction,
  BaseProcessedTransaction,
  BaseProgressingTransaction,
  BaseSentTransaction,
  BaseSuccessTransaction,
  BaseTimeoutTransaction,
} from "./types";

export type TransactionApiType = "marketBuy" | "marketSell";

export type TransactionApiErrorCode =
  | "TOKEN_NOT_FOUND" // 400
  | "EXCEEDS_PRICE_IMPACT" // 400
  | "STABLE_COINS_NOT_SUPPORTED" // 400
  | "NO_ROUTES_FOUND" // 404
  | "INTERNAL_ERROR" // 500
  | "VALIDATION_ERROR"; // 400

export type TransactionMessageErrorCode =
  | "SLIPPAGE"
  | "INSUFFICIENT_FUNDS"
  | "POOL_DISABLED"
  | "SOLANA_CONGESTED"
  | "INTERNAL_ERROR"
  | "NOT_AUTHORIZED"
  | "TOKEN_NOT_FOUND"
  | "TRANSIENT_ERROR"; // signer related error mostly visible on develop

// i assume those are related to swap only
export type SwapErrorCodes =
  | TransactionApiErrorCode
  | TransactionMessageErrorCode;

type BaseSwapTransaction = {
  timestamp: number;
  inputAmount: string;
  tokenTicker: string;
  type: TransactionApiType;
};

export type InitialSwapTransaction = BaseInitialTransaction<"swap"> &
  BaseSwapTransaction;

export type ProgressingSwapTransaction = BaseProgressingTransaction<"swap"> &
  BaseSwapTransaction;

export type SuccessSwapTransaction = BaseSuccessTransaction<"swap"> & {
  txId: string;
  outputAmount: string;
  swapDuration: number; // Duration of the swap in milliseconds
} & BaseSwapTransaction;

export type ProcessedSwapTransaction = BaseProcessedTransaction<"swap"> & {
  txId: string;
  outputAmount: string;
  swapDuration: number;
} & BaseSwapTransaction;

export type SentSwapTransaction = BaseSentTransaction<"swap"> & {
  txId: string;
} & BaseSwapTransaction;

export type TimeoutSwapTransaction = BaseTimeoutTransaction<"swap"> & {
  txId: string;
} & BaseSwapTransaction;

export type ErrorSwapTransaction = BaseErrorTransaction<
  "swap",
  SwapErrorCodes
> & {
  txId?: string;
} & BaseSwapTransaction;

export type SwapTransaction =
  | InitialSwapTransaction
  | SuccessSwapTransaction
  | ErrorSwapTransaction
  | ProgressingSwapTransaction

