type PartiallyFilledStatus = {
  orderStatus: "partially-filled";
  partiallyFilledPct: number | null;
};

type UnfulfilledStatus = {
  orderStatus: "unfulfilled";
};

type AutoStratStatus =
  | {
      orderStatus: "cancelled" | "expired" | "fulfilled";
    }
  | UnfulfilledStatus
  | PartiallyFilledStatus;

export type LimitOrderType =
  | "dca-buy"
  | "dca-sell"
  | "buy-breakout"
  | "buy-dip"
  | "stop-loss"
  | "take-profit"
  | "trailing-stop-loss";

type BaseLimitOrder = {
  id: number;
  amount: number;
  amountUsd: string;
  amountSol: string;
  createdAt: number;
  triggerUsdMcap: string;
  transactionHash?: string | null;
  limitOrderType: LimitOrderType;
  trigger?: string | null;
  updatedTrigger?: string | null;
  signerWalletId?: string;
};

type GroupOrder = {
  recordType: "group_order";
  entityId: string;
  latestOrderCreatedAt: string;
  tokenInId: string;
  tokenOutId: string;
  tokenInAddress: string;
  tokenOutAddress: string;
  tokenInName: string;
  tokenOutName: string;
  tokenInTicker: string;
  tokenOutTicker: string;
  limitOrderGroupId: string;
  orderType: "limit-group";
  autoStratId?: null;
  autoStratName?: null;
  autoStratPriorityFee?: null;
  autoStratPriorityFeeLevel?: null;
  autoStratMevType?: null;
  autoStratSlippageBps?: null;
  limitOrders: Array<
    BaseLimitOrder & (UnfulfilledStatus | PartiallyFilledStatus)
  >;
  signerWalletId?: string;
};

type IndividualOrder = {
  recordType: "individual_order";
  tokenInName: string;
  tokenOutName: string;
  tokenInTicker: string;
  tokenOutTicker: string;
  autoStratId: null;
  tokenInAddress: string;
  tokenOutAddress: string;
  entityId: string;
  limitOrders: [BaseLimitOrder & (UnfulfilledStatus | PartiallyFilledStatus)];
  signerWalletId?: string;
};

type AutoStratLimitOrder = Omit<IndividualOrder, "limitOrders"> &
