import { injectable } from 'inversify';
import {
  BotEnvironment,
  DbEnvironment,
  IConfigService,
} from './interfaces/config.js';

import convict from 'convict';
import convict_format_with_validator from 'convict-format-with-validator';
import { IDbConfigService } from 'db';
import * as dotenv from 'dotenv';
import { ISignerConfigService } from 'signer';
import { CronJobType, parseCronJobTypes } from './interfaces/cron-job.js';
import {
  parseTokenBalancesFetchMode,
  TokenBalancesFetchMode,
} from './solana/interfaces/token-balances.js';

convict.addFormats(convict_format_with_validator);
convict.addFormat({
  name: 'non-empty-string',
  validate: function (val) {
    if (val === '') {
      throw new Error('String must be non-empty');
    }
  },
  coerce: function (val) {
    return val.trim();
  },
});

dotenv.config();

const config = convict({
  BOT_TOKEN_SECRET_NAME: {
    format: 'non-empty-string',
    default: '',
    env: 'BOT_TOKEN_SECRET_NAME',
