{
  "$schema": "https://json.schemastore.org/claude-code-settings.json",
  "_envDocs": {
    "description": "Platform-agnostic environment variables used throughout the Personal AI system",
    "DA": "Digital Assistant name - your AI's identity (e.g., 'Kai', 'PAI', 'Nova'). Used in statusline, hooks, and agent identification.",
    "PAI_DIR": "Root directory for all PAI configuration. The setup.sh script updates this automatically. If hooks fail with 'No such file or directory', update this to your actual path (e.g., /Users/john/.claude).",
    "PAI_SIMPLE_COLORS": "Set to '1' to use basic ANSI colors in statusline instead of 24-bit RGB. Enable if you see garbled colors or display issues in your terminal. Default: 0 (use full color).",
    "CLAUDE_CODE_MAX_OUTPUT_TOKENS": "Maximum tokens for Claude responses. Higher values allow longer outputs but cost more."
  },
  "_setupNote": "IMPORTANT: Run 'bash .claude/setup.sh' to automatically configure PAI_DIR. If you see hook errors mentioning '__HOME__', the setup script was not run or you need to manually change PAI_DIR below to your actual home directory path (e.g., /Users/john/.claude on macOS or /home/john/.claude on Linux).",
  "env": {
    "PAI_DIR": "/Users/neilhart/.claude",
    "DA": "PAI",
    "PAI_SIMPLE_COLORS": "0",
    "CLAUDE_CODE_MAX_OUTPUT_TOKENS": "64000"
  },
  "permissions": {
    "allow": [
      "Bash",
      "Read",
      "Write",
      "Edit",
      "MultiEdit",
      "Glob",
      "Grep",
      "WebFetch(domain:*)",
      "WebSearch",
      "NotebookRead",
      "NotebookEdit",
      "TodoWrite",
      "ExitPlanMode",
      "Task",
      "mcp__*"
    ],
    "deny": [
      "Bash(diskutil partitionDisk)",
      "Bash(diskutil eraseDisk)",
      "Bash(diskutil eraseVolume)",
      "Bash(diskutil reformat)",
      "Bash(diskutil zeroDisk)",
      "Bash(diskutil appleRAID)",
      "Bash(diskutil randomDisk)",
      "Bash(diskutil coreStorage)",
      "Bash(diskutil cs)",
      "Bash(diskutil disableJournal)",
      "Bash(diskutil resetFusion)",
      "Bash(diskutil disableOwnership)",
      "Bash(diskutil apfs deleteContainer)",
      "Bash(diskutil apfs eraseVolume)",
      "Bash(diskutil apfs decryptVolume)",
      "Bash(diskutil apfs changePassphrase)",
      "Bash(diskutil apfs deleteVolume)",
      "Bash(diskutil apfs deleteVolumeGroup)",
      "Bash(diskutil apfs deleteSnapshot)",
      "Bash(rm -rf /)",
      "Bash(rm -rf /*)",
      "Bash(rm -rf ~)",
      "Bash(rm -rf $HOME)",
      "Bash(rm -rf $PAI_HOME)",
      "Bash(rm -rf $PAI_DIR)",
      "Bash(sudo rm -rf /)",
      "Bash(sudo rm -rf /*)",
      "Bash(fork bomb)",
      "Bash(dd if=/dev/zero of=/dev/sda)",
      "Bash(mkfs.ext4 /dev/sda)",
      "Bash( /dev/sda)",
      "Bash(> /dev/sda)"
    ]
  },
  "enableAllProjectMcpServers": true,
  "enabledMcpjsonServers": [],
  "hooks": {
    "PreToolUse": [
      {
        "matcher": "Bash",
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/security-validator.ts"
          }
        ]
      },
      {
        "matcher": "*",
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type PreToolUse"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "*",
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type PostToolUse"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/update-tab-on-action.ts"
          }
        ]
      }
    ],
    "SessionEnd": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-session-summary.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type SessionEnd"
          }
        ]
      }
    ],
    "UserPromptSubmit": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/update-tab-titles.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type UserPromptSubmit"
          }
        ]
      }
    ],
    "SessionStart": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/load-core-context.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/initialize-session.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type SessionStart"
          }
        ]
      }
    ],
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/stop-hook.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type Stop"
          }
        ]
      }
    ],
    "SubagentStop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/subagent-stop-hook.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type SubagentStop"
          }
        ]
      }
    ],
    "PreCompact": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/context-compression-hook.ts"
          },
          {
            "type": "command",
            "command": "${PAI_DIR}/Hooks/capture-all-events.ts --event-type PreCompact"
          }
        ]
      }
    ]
  },
  "statusLine": {
    "type": "command",
    "command": "bash ${PAI_DIR}/statusline-command.sh"
  },
  "enabledPlugins": {
    "browser-automation@browser-tools": true
  },
  "alwaysThinkingEnabled": true,
  "max_tokens": 4096
}

{
  "mcpServers": {
    "httpx": {
      "type": "http",
      "description": "Use for getting information on web servers or site stack information, like what tech stack a given website is running, and other similar use cases.",
      "url": "https://httpx-mcp.danielmiessler.workers.dev",
      "headers": {
        "x-api-key": "REDACTED"
      }
    },
    "content": {
      "type": "http",
      "description": "Figuring out any of my (Daniel Miessler's) opinions or blog posts or writing online. This is basically an archive of all my content.",
      "url": "https://content-mcp.danielmiessler.workers.dev"
    },
    "daemon": {
      "type": "http",
      "description": "My personal API for everything in my (Daniel Miessler's) life.",
      "url": "https://mcp.daemon.danielmiessler.com"
    },
    "Foundry": {
      "type": "http",
      "description": "My personal AI infrastructure (PAI), which is where you should look for any tool where I ask you to do a task that you're not sure how to do. Start by checking here to see if there's a tool for it.",
      "url": "https://api.danielmiessler.com/mcp/",
      "headers": {
        "Authorization": "REDACTED"
      }
    },
    "naabu": {
      "type": "http",
      "description": "This is a port scanner for finding open ports or services on hosts.",
      "url": "https://naabu-mcp.danielmiessler.workers.dev",
      "headers": {
        "x-api-key": "REDACTED"
      }
    },
    "brightdata": {
      "command": "bunx",
      "args": [
        "-y",
        "@brightdata/mcp"
      ],
      "env": {
        "API_TOKEN": "REDACTED"
      }
    },
    "stripe": {
      "command": "bunx",
      "args": [
        "-y",
        "@stripe/mcp",
        "--tools=all"
      ],
      "description": "Stripe payment processing and financial operations"
    },
    "Ref": {
      "command": "bunx",
      "args": [
        "@refdotai/mcp"
      ],
      "description": "Documentation search and reading from public sources and private repositories"
    },
    "apify": {
      "command": "bunx",
      "args": [
        "@apify/actors-mcp-server"
      ],
      "env": {
        "APIFY_TOKEN": "REDACTED"
      },
      "description": "Web scraping and automation tools via Apify platform"
    },
    "playwright": {
      "command": "bunx",
      "args": [
        "@playwright/mcp@latest",
        "--extension"
      ],
      "description": "Browser automation and testing using Playwright for visual debugging and web interaction"
    }
  }
}

{
  "$schema": "https://json-schema.org/draft-07/schema#",
  "description": "PAI Protected Files - Files that must NOT be overwritten with Kai content",
  "version": "1.0",
  "protected": {
    "core_documents": {
      "description": "Core PAI documentation that differs from Kai",
      "files": [
        "README.md",
        "PAI_CONTRACT.md",
        "SECURITY.md"
      ],
      "validation": "Must contain 'PAI' or 'Personal AI Infrastructure' in content"
    },
    "pai_infrastructure": {
      "description": "PAI-specific infrastructure code",
      "files": [
        ".claude/hooks/lib/pai-paths.ts",
        ".claude/hooks/self-test.ts",
        ".pai-protected.json"
      ],
      "validation": "Must not contain references to private Kai data"
    },
    "sanitized_config": {
      "description": "Configuration files that must remain sanitized",
      "files": [
        ".claude/.env.example",
        ".claude/settings.json"
      ],
      "validation": "Must not contain API keys, personal emails, or secrets"
    },
    "protected_patterns": {
      "description": "String patterns that should never appear in PAI",
      "patterns": [
        "daniel@danielmiessler.com",
        "susan@danielmiessler.com",
        "ANTHROPIC_API_KEY=sk-",
        "PERPLEXITY_API_KEY=[^E]",
        "ELEVENLABS_API_KEY=[^y]",
        "/Users/daniel/.claude/skills/personal",
        "daemon.plist"
      ],
      "exception_files": [
        "SECURITY.md",
        ".pai-protected.json",
        "PAI_SYNC_GUIDE.md",
        ".claude/.env.example"
      ]
    }
  },
  "kai_to_pai_workflow": {
    "description": "How to safely sync Kai improvements to PAI",
    "steps": [
      "1. Make changes in Kai (${PAI_DIR}/)",
      "2. Test thoroughly in Kai environment",
      "3. Identify which changes should go to public PAI",
      "4. Copy changes to PAI repo (${PAI_REPO}/)",
      "5. SKIP protected files (they stay PAI-specific)",
      "6. Sanitize any personal data or API keys",
      "7. Run: bun ${PAI_REPO}/.claude/hooks/self-test.ts",
      "8. Run: bun ${PAI_REPO}/.claude/hooks/validate-protected.ts",
      "9. If validation passes, commit to PAI",
      "10. If validation fails, check which protected files changed"
    ]
  }
}

