// @ts-nocheck
'use client';
import * as React from 'react';
import styles from './page.module.css';
import { useState } from 'react';
import {
  InfoSnackbar,
  WarningSnackbar,
  ErrorSnackbar,
  SuccessSnackbar,
} from './util/snackbar';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { DaoActionView } from './dao/dao-action';
import { UserActionView } from './user/user-action';
import IncineratorView from './incinerator/incinerator';
import { DaoInfoView } from './dao/dao-info';
import { NotificationView } from './notification/notification';
import { DaoListView } from './dao/dao-list';

const environmentTitles = {
  dev: 'Internal Tool (Development)',
  beta: 'Internal Tool (Beta)',
  prod: 'Internal Tool (Production)',
};

const pageTitle = environmentTitles[process.env.NEXT_PUBLIC_ENV];

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function a11yProps(index: number) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

function CustomTabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

export default function Home() {
  const [errorMessage, setErrorMessage] = useState('Error in response!');
  const [successMessage, setSuccessMessage] = useState('Success!');
  const [infoOpen, setInfoOpen] = useState(false);
  const [warningOpen, setWarningOpen] = useState(false);
  const [errorOpen, setErrorOpen] = useState(false);
  const [successOpen, setSuccessOpen] = useState(false);
  const [tabValue, setTabValue] = useState(0);

  const handleChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  function handleInfoClose(
    event: React.SyntheticEvent | Event,
    reason?: string,
  ) {
    if (reason === 'clickaway') {
      return;
    }
    setInfoOpen(false);
  }

  function handleWarningClose(
    event: React.SyntheticEvent | Event,
    reason?: string,
  ) {
    if (reason === 'clickaway') {
      return;
    }
    setWarningOpen(false);
  }

  function handleErrorClose(
    event: React.SyntheticEvent | Event,
    reason?: string,
  ) {
    if (reason === 'clickaway') {
      return;
    }
    setErrorOpen(false);
  }

  function handleSuccessClose(
    event: React.SyntheticEvent | Event,
    reason?: string,
  ) {
    if (reason === 'clickaway') {
      return;
    }
    setSuccessOpen(false);
  }

  return (
    <main className={styles.main}>
      <div className={styles.center}>
        <Box sx={{ width: '100%' }}>
          <Box
            sx={{
              display: 'flex',
              width: '100%',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'primary.main',
            }}
          >
            <h1>{pageTitle}</h1>
          </Box>
          <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
            <Tabs
              value={tabValue}
              onChange={handleChange}
              TabIndicatorProps={{ style: { display: 'none' } }}
              centered
              aria-label="basic tabs example"
            >
              <Tab label="User" {...a11yProps(0)} />
              <Tab label="DAO Creator" {...a11yProps(1)} />
              <Tab label="DAO List" {...a11yProps(2)} />
              <Tab label="DAO Info" {...a11yProps(3)} />
              <Tab label="Notifications" {...a11yProps(4)} />
              {/* <Tab label="Incinerator" {...a11yProps(2)} /> */}
            </Tabs>
          </Box>
          <CustomTabPanel value={tabValue} index={0}>
            <UserActionView
              setErrorMessage={setErrorMessage}
              setErrorOpen={setErrorOpen}
              setInfoOpen={setInfoOpen}
              setSuccessOpen={setSuccessOpen}
              setSuccessMessage={setSuccessMessage}
              setWarningOpen={setWarningOpen}
            />
          </CustomTabPanel>
          <CustomTabPanel value={tabValue} index={1}>
            <DaoActionView
              setErrorMessage={setErrorMessage}
              setErrorOpen={setErrorOpen}
              setInfoOpen={setInfoOpen}
              setSuccessOpen={setSuccessOpen}
              setSuccessMessage={setSuccessMessage}
              setWarningOpen={setWarningOpen}
            />
          </CustomTabPanel>
          <CustomTabPanel value={tabValue} index={2}>
            <DaoListView
              setErrorMessage={setErrorMessage}
              setErrorOpen={setErrorOpen}
              setInfoOpen={setInfoOpen}
              setSuccessOpen={setSuccessOpen}
              setSuccessMessage={setSuccessMessage}
              setWarningOpen={setWarningOpen}
            />
          </CustomTabPanel>
          <CustomTabPanel value={tabValue} index={3}>
            <DaoInfoView
              setErrorMessage={setErrorMessage}
              setErrorOpen={setErrorOpen}
              setInfoOpen={setInfoOpen}
              setSuccessOpen={setSuccessOpen}
              setSuccessMessage={setSuccessMessage}
              setWarningOpen={setWarningOpen}
            />
          </CustomTabPanel>
          <CustomTabPanel value={tabValue} index={4}>
            <NotificationView
              setErrorMessage={setErrorMessage}
              setErrorOpen={setErrorOpen}
              setInfoOpen={setInfoOpen}
              setSuccessOpen={setSuccessOpen}
              setSuccessMessage={setSuccessMessage}
              setWarningOpen={setWarningOpen}
            />
          </CustomTabPanel>
          {/* <CustomTabPanel value={tabValue} index={2}>
            <IncineratorView />
          </CustomTabPanel> */}
        </Box>
      </div>

      <InfoSnackbar infoOpen={infoOpen} handleInfoClose={handleInfoClose} />
      <WarningSnackbar
        warningOpen={warningOpen}
        handleWarningClose={handleWarningClose}
      />
      <ErrorSnackbar
        errorMessage={errorMessage}
        errorOpen={errorOpen}
        handleErrorClose={handleErrorClose}
      />
      <SuccessSnackbar
        successMessage={successMessage}
        successOpen={successOpen}
        handleSuccessClose={handleSuccessClose}
      />
    </main>
  );
}

