     8→
     9→export class UserRouter {
    10→  private userLogic: UserLogic;
    11→
    12→  constructor(private fastify: fastify.FastifyInstance) {
    13→    this.userLogic = new UserLogic();
    14→    this.initialzeUserRoutes();
