    17→  initialzeUserRoutes() {
    18→    this.fastify.post(
    19→      '/update/referralCode',
    20→      {
    21→        schema: {
    22→          body: {
    23→            type: 'object',
    24→            properties: {
    25→              oldReferralCode: { type: 'string' },
    26→              newReferralCode: { type: 'string' },
    27→            },
    28→          },
    29→        },
    30→      },
    31→      async (
    32→        request: FastifyRequest<{
    33→          Body: { oldReferralCode: string; newReferralCode: string };
    34→        }>,
    35→        reply,
    36→      ) => {
    37→        const { oldReferralCode, newReferralCode } = request.body;
    38→        var result = false;
