    53→              message =
    54→                'Internal server error (probably database connection error)';
    55→            }
    56→          }
    57→        }
    58→
    59→        if (!result) {
    60→          reply.code(400).send({ success: false, message: message });
    61→        } else {
    62→          reply.code(200).send({ success: true, message: message });
    63→        }
    64→      },
    65→    );
    66→
    67→    this.fastify.post(
    68→      '/user/earlyAccess',
    69→      {
    70→        schema: {
    71→          body: {
    72→            type: 'object',
    73→            properties: {
    74→              userId: { type: 'string' },
    75→            },
    76→          },
    77→        },
    78→      },
    79→      async (
    80→        request: FastifyRequest<{
    81→          Body: { userId: string };
