    95→          if (error instanceof Error) {
    96→            console.error(error);
    97→            message =
    98→              'Internal server error (probably database connection error)';
    99→          }
   100→        }
   101→
   102→        if (!result) {
   103→          reply.code(400).send({ success: false, message: message });
   104→        } else {
   105→          reply.code(200).send({ success: true, message: message });
   106→        }
   107→      },
   108→    );
   109→
   110→    this.fastify.post(
   111→      '/user/refer',
   112→      {
   113→        schema: {
   114→          body: {
   115→            type: 'object',
   116→            properties: {
   117→              userId: { type: 'string' },
