import fastify, { FastifyRequest } from 'fastify';
import {
  MembershipTier,
  ReferralType,
} from '../infrastructure/db/interfaces/types.js';
import UserLogic from '../request-logic/user.js';
import { AppLogicResponse } from '../request-logic/utils.js';

export class UserRouter {
  private userLogic: UserLogic;

  constructor(private fastify: fastify.FastifyInstance) {
    this.userLogic = new UserLogic();
    this.initialzeUserRoutes();
  }

  initialzeUserRoutes() {
    this.fastify.post(
      '/update/referralCode',
      {
        schema: {
          body: {
            type: 'object',
            properties: {
              oldReferralCode: { type: 'string' },
              newReferralCode: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Body: { oldReferralCode: string; newReferralCode: string };
        }>,
        reply,
      ) => {
        const { oldReferralCode, newReferralCode } = request.body;
        var result = false;
        var message = '';

        if (oldReferralCode && newReferralCode) {
          try {
            const appLogicResponse: AppLogicResponse =
              await this.userLogic.updateReferralTokenByRefCode(
                oldReferralCode,
                newReferralCode,
              );
            result = appLogicResponse.success;
            message = appLogicResponse.message;
          } catch (error) {
            if (error instanceof Error) {
              console.error(error);
              message =
                'Internal server error (probably database connection error)';
            }
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({ success: true, message: message });
        }
      },
    );

    this.fastify.post(
      '/user/earlyAccess',
      {
        schema: {
          body: {
            type: 'object',
            properties: {
              userId: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Body: { userId: string };
        }>,
        reply,
      ) => {
        const { userId } = request.body;
        let result = false;
        let message = '';

        try {
          const appLogicResponse: AppLogicResponse =
            await this.userLogic.grantUserEarlyAccess(userId);
          result = appLogicResponse.success;
          message = appLogicResponse.message;
        } catch (error) {
          if (error instanceof Error) {
            console.error(error);
            message =
              'Internal server error (probably database connection error)';
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({ success: true, message: message });
        }
      },
    );

    this.fastify.post(
      '/user/refer',
      {
        schema: {
          body: {
            type: 'object',
            properties: {
              userId: { type: 'string' },
              referralCode: { type: 'string' },
              referrerUserId: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Body: {
            userId: string;
            referralCode?: string;
            referrerUserId?: string;
          };
        }>,
        reply,
      ) => {
        const { userId, referralCode, referrerUserId } = request.body;
        let result = false;
        let message = '';

        try {
          const appLogicResponse: AppLogicResponse =
            await this.userLogic.grantAccessAndReferUser(
              userId,
              referralCode,
              referrerUserId,
            );
          result = appLogicResponse.success;
          message = appLogicResponse.message;
        } catch (error) {
          if (error instanceof Error) {
            console.error(error);
            message =
              'Internal server error (probably database connection error)';
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({ success: true, message: message });
        }
      },
    );

    this.fastify.patch(
      '/user/referralType',
      {
        schema: {
          body: {
            type: 'object',
            properties: {
              referralId: { type: 'string' },
              referralType: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Body: { referralId: string; referralType: ReferralType };
        }>,
        reply,
      ) => {
        const { referralId, referralType } = request.body;
        var result = false;
        var message = '';

        if (referralId && referralType) {
          try {
            const appLogicResponse: AppLogicResponse =
              await this.userLogic.updateReferralType(referralId, referralType);
            result = appLogicResponse.success;
            message = appLogicResponse.message;
          } catch (error) {
            if (error instanceof Error) {
              console.error(error);
              message =
                'Internal server error (probably database connection error)';
            }
          }
        } else {
          console.error('Invalid referral type update parameters!');
          message = 'Invalid referral type update parameters!';
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({ success: true, message: message });
        }
      },
    );

    this.fastify.patch(
      '/user',
      {
        schema: {
          body: {
            type: 'object',
            properties: {
              referralCode: { type: 'string' },
              isPartner: { type: 'boolean' },
              membershipTier: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Body: {
            referralCode: string;
            isPartner: boolean;
            membershipTier: MembershipTier;
          };
        }>,
        reply,
      ) => {
        const { referralCode, isPartner, membershipTier } = request.body;
        var result = false;
        var message = '';

        if (referralCode && isPartner !== undefined) {
          try {
            const appLogicResponse: AppLogicResponse =
              await this.userLogic.updatePartnerStatusAndMembershipTierByRefCode(
                referralCode,
                isPartner,
                membershipTier,
              );
            result = appLogicResponse.success;
            message = appLogicResponse.message;
          } catch (error) {
            if (error instanceof Error) {
              console.error(error);
              message =
                'Internal server error (probably database connection error)';
            }
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({ success: true, message: message });
        }
      },
    );

    this.fastify.get('/user/vip', async (_request: FastifyRequest, reply) => {
      try {
        const appLogicResponse: AppLogicResponse =
          await this.userLogic.getAllVIPUsers();
        reply.code(200).send({
          success: appLogicResponse.success,
          message: appLogicResponse.message,
          resultObject: appLogicResponse.result,
        });
      } catch (error) {
        if (error instanceof Error) {
          console.error(error);
          reply.code(500).send({ success: false, message: error.message });
        }
      }
    });

    this.fastify.get(
      '/user/refcode',
      {
        schema: {
          querystring: {
            type: 'object',
            properties: {
              referralCode: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Querystring: { referralCode: string };
        }>,
        reply,
      ) => {
        const { referralCode } = request.query;
        var result = false;
        var message = '';
        var resultObject = null;

        if (referralCode) {
          try {
            const appLogicResponse: AppLogicResponse =
              await this.userLogic.getUserInfoByReferralCode(referralCode);
            result = appLogicResponse.success;
            message = appLogicResponse.message;
            resultObject = appLogicResponse.result;
          } catch (error) {
            if (error instanceof Error) {
              console.error(error);
              message =
                'Internal server error (probably database connection error)';
            }
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({
            success: true,
            message: message,
            resultObject: resultObject,
          });
        }
      },
    );

    this.fastify.get(
      '/user/tgUserName',
      {
        schema: {
          querystring: {
            type: 'object',
            properties: {
              tgUserName: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Querystring: { tgUserName: string };
        }>,
        reply,
      ) => {
        const { tgUserName } = request.query;
        var result = false;
        var message = '';
        var resultObject = null;

        if (tgUserName) {
          try {
            const appLogicResponse: AppLogicResponse =
              await this.userLogic.getUserInfoByTgUserName(tgUserName);
            result = appLogicResponse.success;
            message = appLogicResponse.message;
            resultObject = appLogicResponse.result;
          } catch (error) {
            if (error instanceof Error) {
              console.error(error);
              message =
                'Internal server error (probably database connection error)';
            }
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({
            success: true,
            message: message,
            resultObject: resultObject,
          });
        }
      },
    );

    this.fastify.get(
      '/user/tgUserId',
      {
        schema: {
          querystring: {
            type: 'object',
            properties: {
              tgUserId: { type: 'string' },
            },
          },
        },
      },
      async (
        request: FastifyRequest<{
          Querystring: { tgUserId: string };
        }>,
        reply,
      ) => {
        const { tgUserId } = request.query;
        var result = false;
        var message = '';
        var resultObject = null;

        if (tgUserId) {
          try {
            const appLogicResponse: AppLogicResponse =
              await this.userLogic.getUserInfoByTgUserId(tgUserId);
            result = appLogicResponse.success;
            message = appLogicResponse.message;
            resultObject = appLogicResponse.result;
          } catch (error) {
            if (error instanceof Error) {
              console.error(error);
              message =
                'Internal server error (probably database connection error)';
            }
          }
        }

        if (!result) {
          reply.code(400).send({ success: false, message: message });
        } else {
          reply.code(200).send({
            success: true,
            message: message,
            resultObject: resultObject,
          });
        }
      },
    );
  }
}

