import { injectable } from 'inversify';
import { IConfigService } from './interfaces/config.js';

import convict from 'convict';
import convict_format_with_validator from 'convict-format-with-validator';
import * as dotenv from 'dotenv';
convict.addFormats(convict_format_with_validator);

dotenv.config();

var db_host = '127.0.0.1';
var db_pw = '';

const isProd = process.env.NODE_ENV === 'prod';
const isBeta = process.env.NODE_ENV === 'beta';

if (isProd) {
  db_host = '10.9.112.7';
} else if (isBeta) {
  db_host = '10.16.144.3';
  db_pw = '';
}

const config = convict({
  BOT_TOKEN_SECRET_NAME: {
    format: String,
    default: '',
    env: 'BOT_TOKEN_SECRET_NAME',
  },
  RPC_URL_SECRET_NAME: {
    format: String,
    default: '',
    env: 'RPC_URL_SECRET_NAME',
  },
  GEYSER_URL: {
    format: String,
    default: 'https://api.rpcpool.com',
    env: 'GEYSER_URL',
  },
  GEYSER_TOKEN_SECRET_NAME: {
    format: String,
    default: '',
    env: 'GEYSER_TOKEN_SECRET_NAME',
  },
  DB_NAME: {
    format: String,
    default: 'bonkbot',
    env: 'DB_NAME',
  },
  DB_HOST: {
    format: String,
    default: db_host,
    env: 'DB_HOST',
  },
  DB_PASSWORD_SECRET_NAME: {
    format: String,
    default: db_pw,
    env: 'INTERNAL_TOOL_DB_PASSWORD_SECRET_NAME',
  },
  DB_PORT: {
    format: 'port',
    default: 5432,
    env: 'DB_PORT',
  },
  DB_USER: {
    format: String,
    default: 'internal_tools',
    env: 'DB_USER',
  },
  DB_CONNECTIONS: {
    format: 'nat',
    default: 10,
    env: 'DB_CONNECTIONS',
  },
  PRETTY_LOGS: {
    format: 'Boolean',
    default: false,
    env: 'PRETTY_LOGS',
  },
  LOG_LEVEL: {
    format: ['trace', 'debug', 'info', 'warn', 'error', 'fatal'],
    default: 'info',
    env: 'LOG_LEVEL',
  },
  DB_SECRET_KEY_NAME: {
    format: String,
    default: '',
    env: 'DB_SECRET_KEY_NAME',
  },
  BOT_FEE_WALLET_SECRET_NAME: {
    format: String,
    default: 'projects/1034486134720/secrets/fee-wallet/versions/latest',
    env: 'BOT_FEE_WALLET_SECRET_NAME',
  },
  BOT_REFERRALS_WALLET_SECRET_NAME: {
    format: String,
    default: 'projects/1034486134720/secrets/referrals-wallet/versions/latest',
    env: 'BOT_REFERRALS_WALLET_SECRET_NAME',
  },
  BOT_ENV: {
    format: ['dev', 'beta', 'prod'],
    default: 'dev',
    env: 'BOT_ENV',
  },
  BOT_FEE_DENOMINATOR: {
    format: 'nat',
    default: 100,
    env: 'BOT_FEE_DENOMINATOR',
  },
  BOT_TG_HANDLE: {
    format: String,
    default: 'bonkbot_bot',
    env: 'BOT_TG_HANDLE',
  },
  BOT_NAME: {
    format: String,
    default: '',
    env: 'BOT_NAME',
  },
  RATE_LIMIT_THRESHHOLD: {
    format: 'nat',
    default: 5,
