  },
  HIDE_WSOL: {
    format: 'Boolean',
    default: false,
    env: 'HIDE_WSOL',
  },
  TOKEN_PRICE_CACHE_TTL: {
    format: 'nat',
    default: 1500,
    env: 'TOKEN_PRICE_CACHE_TTL',
  },
  ACCOUNT_CACHE_SIZE: {
    format: 'nat',
    default: 30000,
    env: 'ACCOUNT_CACHE_SIZE',
  },
  JUPITER_PRICE_TTL: {
    format: 'nat',
    default: 1000,
    env: 'JUPITER_PRICE_TTL',
  },
  JUPITER_PRICE_QUOTE_AMOUNT_IN_LAMPORTS: {
