  },
  POOL_UPDATE_WORKERS_CONNECTED: {
    format: 'nat',
    default: 10,
    env: 'POOL_UPDATE_WORKERS_CONNECTED',
  },
  JITO_BLOCK_ENGINE_URL: {
    format: String,
    default: 'amsterdam.mainnet.block-engine.jito.wtf',
    env: 'JITO_BLOCK_ENGINE_URL',
  },
  JITO_BLOCK_ENGINE_PK_SECRET_NAME: {
    format: String,
    default: '',
    env: 'JITO_BLOCK_ENGINE_PK_SECRET_NAME',
  },
  MEDIUM_PRIORITY_FEE_IN_LAMPORTS: {
    format: 'nat',
    default: 1_500_000,
    env: 'MEDIUM_PRIORITY_FEE_IN_LAMPORTS',
  },
  HIGH_PRIORITY_FEE_IN_LAMPORTS: {
    format: 'nat',
    default: 7_500_000,
    env: 'HIGH_PRIORITY_FEE_IN_LAMPORTS',
  },
  VERY_HIGH_PRIORITY_FEE_IN_LAMPORTS: {
    format: 'nat',
    default: 15_000_000,
    env: 'VERY_HIGH_PRIORITY_FEE_IN_LAMPORTS',
  },
  WITHDRAWAL_PRIORITY_FEE_IN_LAMPORTS: {
    format: 'nat',
    default: 200,
    env: 'WITHDRAWAL_PRIORITY_FEE_IN_LAMPORTS',
  },
  WEBHOOK_LOAD_BALANCER_DOMAIN: {
    format: String,
    default: '',
    env: 'WEBHOOK_LOAD_BALANCER_DOMAIN',
  },
  LOAD_BALANCER_CERTIFICATE_PATH: {
    format: String,
    default: '',
    env: 'LOAD_BALANCER_CERTIFICATE_PATH',
  },
  TG_BOT_API_WEBHOOK_SECRET_NAME: {
    format: String,
    default: '',
    env: 'TG_BOT_API_WEBHOOK_SECRET_NAME',
  },
  USE_LONG_POLLING: {
    format: 'Boolean',
    default: false,
    env: 'USE_LONG_POLLING',
  },
  REDIS_URL: {
    format: String,
    default: 'redis://127.0.0.1:6379',
    env: 'REDIS_URL',
  },
  BOT_FINGERPRINT: {
    format: String,
    default: '',
    env: 'BOT_FINGERPRINT',
  },
  POOLS_ENABLED: {
    format: 'Boolean',
    default: false,
    env: 'POOLS_ENABLED',
  },
});

config.validate({ allowed: 'strict' });

