import Fastify from 'fastify';
import { JsonSchemaToTsProvider } from '@fastify/type-provider-json-schema-to-ts';
import cors from '@fastify/cors';
import fs from 'fs';
import { UserRouter } from './src/routing/user.js';
import { DaoRouter } from './src/routing/dao.js';
import { NotificationRouter } from './src/routing/notification.js';

const isProd = process.env.NODE_ENV === 'prod';
const isBeta = process.env.NODE_ENV === 'beta';

var https = null;

if (isProd) {
  https = {
    key: fs.readFileSync(
      '/usr/src/app/certs/internal-tool-1.tail7606f.ts.net.key',
    ),
    cert: fs.readFileSync(
      '/usr/src/app/certs/internal-tool-1.tail7606f.ts.net.crt',
    ),
  };
} else if (isBeta) {
  https = {
    key: fs.readFileSync(
      '/usr/src/app/certs/internal-tool.tail7606f.ts.net.key',
    ),
    cert: fs.readFileSync(
      '/usr/src/app/certs/internal-tool.tail7606f.ts.net.crt',
    ),
  };
}

const fastify = Fastify({
  logger: true,
  https: https,
}).withTypeProvider<JsonSchemaToTsProvider>();

// fastify.addSchema({
//   $id: 'userRequest',
//   type: 'object',
//   properties: {
//     userId: { type: 'string' },
//     newReferralCode: { type: 'string' },
//   },
// });

// fastify.addSchema({
//   $id: 'userReply',
//   type: 'object',
//   properties: {
//     success: { type: 'boolean' },
//     message: { type: 'string' },
//   },
// });

async function runServer() {
  await fastify.register(cors, {
    origin: true,
  });

  new UserRouter(fastify);
  new DaoRouter(fastify);
  new NotificationRouter(fastify);

  // Run the server!
  try {
    await fastify.listen({ host: '0.0.0.0', port: 3010 });
  } catch (err) {
    fastify.log.error(err);
    process.exit(1);
  }
}

runServer();

