import type { ColumnType } from 'kysely';

export type DaoReferralType = 'account-creation' | 'post-account-creation';

export type Generated<T> = T extends ColumnType<infer S, infer I, infer U>
  ? ColumnType<S, I | undefined, U>
  : ColumnType<T, T | undefined, T>;

export type Int8 = ColumnType<
  string,
  string | number | bigint,
  string | number | bigint
>;

export type LimitOrderNotificationType =
  | 'dca_expired_summary'
  | 'generic_error'
  | 'insufficient_sol_for_priority_fee'
  | 'insufficient_token_in_balance'
  | 'insufficient_total_sol_with_fee'
  | 'no_tradeable_pools'
  | 'on_chain_slippage_exceeded'
  | 'price_impact_exceeded'
  | 'sell_order_cancelled'
  | 'slippage_exceeded'
  | 'transaction_did_not_land';

export type LimitOrderType =
  | 'buy-breakout'
  | 'buy-dip'
  | 'dca-buy'
  | 'dca-sell'
  | 'stop-loss'
  | 'take-profit'
  | 'trailing-stop-loss';

export type MembershipTier = 'default' | 'vip';

export type MevType = 'secure' | 'turbo';

export type NotificationMessageType =
  | 'auto_order_cancellation'
  | 'dca'
  | 'default'
  | 'limit_order'
  | 'limit_order_error';

export type NotificationSendStatus = 'processing' | 'sent' | 'unsent';

export type Numeric = ColumnType<string, string | number, string | number>;

export type OrderStatus =
  | 'cancelled'
  | 'expired'
  | 'fulfilled'
  | 'partially-filled'
  | 'unfulfilled';

export type PayoutStatus = 'paid' | 'processing' | 'unpaid';

export type PlatformType = 'nighthawk' | 'telegram' | 'web_terminal';

export type ReferralType =
  | 'dao'
  | 'deeplink'
  | 'default'
  | 'korea-default'
  | 'korea-kol'
  | 'korea-trainer'
  | 'korea-trainer-default'
  | 'partner'
  | 'pre-migration'
  | 'upgraded-partner';

export type SendStatus =
  | 'failed_to_send'
  | 'failed_to_send_retry'
  | 'processing'
  | 'sent'
  | 'unsent';

export type TelegramReferralTierLevel =
  | 'bonkbot-default'
  | 'kr-kol'
  | 'kr-kol-default'
  | 'kr-trainer'
  | 'kr-trainer-default'
  | 'tier1-partner'
